"use strict";

class BigInteger{
  constructor(l){
    this.length = l
    this.bitlength = l*8
    this.bits = "".padStart(l*8,"0")
  }
  setSingleBit(index,value){
    this.bits=this.bits.substr(0,index) + parseInt(value) + this.bits.substr(index*1+1);
  }
  setBits(offset,length,value){
    var binValue = parseInt(value).toString(2).padStart(length,"0")
    for(var i = 0;i<length*1;i++){
      this.setSingleBit(parseInt(offset)+i,binValue[i])
    }
  }
  getValue (start,length) {
    return parseInt(this.bits.substr(start,length),2)
  }
  toArray(base){
    var out=[]
    base= base || 16
    for(var i=0;i<this.bits.length;i+=8){
      var currentbyte = parseInt(this.bits.substr(i,8),2)
      switch(base){
        case "hex":
          base=16
        case 16:
          out.push(currentbyte.toString(base).padStart(2,"0"))
          break
        case "bin":
          base=2
        case 2:
          out.push(currentbyte.toString(base).padStart(8,"0"))
          break
        case "dec":
          base=10
        case 10:
          out.push(currentbyte.toString(base).padStart(3,"0"))
          break
      }
    }
    return out
  }
  toString(base){
    return this.toArray(base).join("")
  }
  static fromHex(str){
    var eep = new BigInteger()
    eep.length=str.length/2
    eep.bitlength=eep.length*8
    eep.bits=[].map.call(str,x=>parseInt(x,16).toString(2).padStart(4,"0")).join("")
    return eep
  }
}

class EnoceanTelegram {
  static teach_in(senderId,eep){
    var eep_a=eep.split("-")
    var func=parseInt(eep_a[1],16)
    var type=parseInt(eep_a[2],16)
    var manu = 0x7f
    var userdata=new BigInteger(4)
    userdata.setBits(0,6,func)
    userdata.setBits(6,7,type)
    userdata.setBits(13,10,manu)
    userdata.setSingleBit(24,1)
    userdata.setSingleBit(28,0)
    var sync = 85
    var ptype= 1
    var length = 10
    var olength = 7
    var head=BigInteger.fromHex(paddedValue(length,4)+paddedValue(olength,2)+paddedValue(ptype,2))
    var headerCRC=CRC8(head.toArray(10))
    var body=BigInteger.fromHex("a5"+userdata+senderId+"0003ffffffffff00")
    return "55"+head.toString()+paddedValue(headerCRC,2)+body.toString()+paddedValue(CRC8(body.toArray(10)),2)
  }
  static encode(j,eepstr){
    var eep = eep_desc[eepstr]
    if(eep==undefined) return null

    if(!Array.isArray(eep.case))eep.case=[eep.case]

    var tel = new EnoceanTelegram()
    var copy = JSON.stringify(j.decoded)
    tel.decoded=JSON.parse(copy)
    tel.decoded.LRNB=j.decoded.LRNB || {value:1}
    tel.encoded= new BigInteger(6+6+8+4).toString(16)
    tel._sync=j._sync || 85
    tel._type=j._type || 1
    tel._optionalLength=j._optionalLength || 7
    tel._packetType=j.packetType || 1
    tel._data=j._data || 0
    tel._senderId=j._senderId || "00112233"

    tel._rssi=255
    tel._securityLevel=0
    tel._subTelNum=3
    tel._destinationId=j._destinationId || "ffffffff"
    tel._optionalData=tel._subTelNum.toString(16).padStart(2,"0")+tel._destinationId+tel._rssi.toString(16).padStart(2,"0")+tel._securityLevel.toString(16).padStart(2,"0")
    if(parseInt(eep.rorg_number,16)==246){
      //f6
      tel._status=j._status || 48
      tel._length=j.length || 7
      tel._rorg=246
    }
    if(parseInt(eep.rorg_number,16)==213){
      //d5
      tel._status=j._status || 0
      tel._length=j.length || 7
      tel._rorg=213
    }
    if(parseInt(eep.rorg_number,16)==165){
      //a5
      tel._status=j._status || 0
      tel._length=j.length || 10
      tel._rorg=165
    }
    var tmp_data =new BigInteger(tel._length-6)
    for(var item in tel.decoded){
      if(!Array.isArray(eep.case[0].datafield))eep.case[0].datafield=[eep.case[0].datafield]
      eep.case[0].datafield.forEach(function(e){
        if(e.shortcut==item) {
          var val = tel.decoded[item].value

          if(e.hasOwnProperty("range")){
            //if(x.scale.hasOwnProperty("ref")) x.scale=x.range
            var B=((e.scale.max*1-e.scale.min*1)/(e.range.max*1-e.range.min*1))
            val   = Math.round(((val-e.scale.min*1)/B)+e.range.min*1)
          }
          tmp_data.setBits(e.bitoffs,e.bitsize,val)
        }
      })
    }
    var header = BigInteger.fromHex(paddedValue(tel._length,4)+paddedValue(tel._optionalLength,2)+paddedValue(tel._type,2))
    tel._headerCRC=CRC8(header.toArray(10))

    tel._userData=tmp_data.toString()
    tel._data=tel._rorg.toString(16).padStart(2,"0")+tel._userData+tel._senderId+tel._status.toString(16).padStart(2,"0")
    var body=BigInteger.fromHex(tel._data+tel._optionalData)
    tel._bodyCRC=CRC8(body.toArray(10))
    tel.encoded=paddedValue(tel._sync,2)+paddedValue(tel._length,4)+paddedValue(tel._optionalLength,2)+paddedValue(tel._type,2)+paddedValue(tel._headerCRC,2)+tel._data+tel._optionalData+paddedValue(tel._bodyCRC,2)
    return tel
  }
  static decode(t,eepstr){
    var tel = new EnoceanTelegram()
    tel.encoded=t
    tel._sync=parseInt(t.substr(0,2),16)
    tel._length=parseInt(t.substr(2,4),16)
    tel._optionalLength=parseInt(t.substr(6,2),16)
    tel._packetType=parseInt(t.substr(8,2),16)
    tel._headerCRC=parseInt(t.substr(10,2),16)
    tel._data=t.substr(12,tel._length*2)
    tel._rorg=parseInt(t.substr(12,2),16)
    tel._userData=t.substr(14,tel._length*2-12)
    tel._optionalData=t.substr(12+tel._length*2,tel._optionalLength*2)
    tel._senderId=tel._data.substr(tel._data.length-10,8)
    tel._status=parseInt(tel._data.substr(tel._data.length-2),16)
    tel._bodyCRC=parseInt(t.substr(t.length-2),16)
    tel._subTelNum=parseInt(tel._optionalData.substr(0,2))
    tel._destinationId=tel._optionalData.substr(2,8)
    tel._rssi=parseInt(tel._optionalData.substr(10,2),16)
    tel._securitLevel=parseInt(tel._optionalData.substr(12,2),16)
    var eep = eep_desc[eepstr]
    if(eep==undefined) return null
    if(!Array.isArray(eep.case))eep.case=[eep.case]
    var td = BigInteger.fromHex(tel._userData)
    eep.case.forEach(function(item){
      if(item.condition!=undefined && item.condition.statusfield!=undefined){
        var s1=new BigInteger(1)
        s1.setSingleBit(item.condition.statusfield[0].bitoffs*1,item.condition.statusfield[0].value*1)
        s1.setSingleBit(item.condition.statusfield[1].bitoffs*1,item.condition.statusfield[1].value*1)
        if(s1.toString(10)==tel._status){
          tel.decoded = EnoceanTelegram.decodeTel(td,item)
        }
      }else{
        tel.decoded = EnoceanTelegram.decodeTel(td,item)
      }
    })
    return tel
  }
  static decodeTel(data,c){
    var ret = {}
    if(!Array.isArray(c.datafield))c.datafield=[c.datafield]
    c.datafield.forEach(function(item){
      var val =  data.getValue(item.bitoffs*1,item.bitsize*1)
      if(item.enum!=undefined && item.enum.item!=undefined){
        for(var i = 0;i<item.enum.item.length;i++){
          if(item.enum.item[i].hasOwnProperty("min")){
            if(item.enum.item[i].min<=val && item.enum.item[i].max >= val){
              ret[item.shortcut]={name:item.data,value:val,desc:item.enum.item[i].description,unit:item.unit}
            }
          }
          if(item.enum.item[i].hasOwnProperty("bitmask")){
            var mask=item.enum.item[i].bitmask
            if((val & mask)==item.enum.item[i].bitvalue){
              ret[item.shortcut]={name:item.data,value:item.enum.item[i].value,desc:item.enum.item[i].description,unit:item.unit}
            }
          }
          if(item.enum.item[i].value==val){
            ret[item.shortcut]={name:item.data,value:val,desc:item.enum.item[i].description,unit:item.unit}
          }
        }
      }
      if(item.hasOwnProperty("range")){
        var rdist = item.range.max*1-item.range.min*1
        var sdist = item.scale.max*1-item.scale.min*1
        var dx = (sdist/rdist)
        var val = (val-item.range.min*1)*dx+item.scale.min*1
        ret[item.shortcut]={name:item.data,value:val,unit:item.unit}
      }
    })

    return ret
  }
}
